// long length. length === 756
var longstra = '';
// same as above
var longstrb = '';

// in v8, just use `+`, and everything would be ok
suite('str_concat', function () {
  bench('+', function () {
    var c = longstra + longstrb
  })

  bench('+=', function () {
    var c = '';
    c += longstra;
    c += longstrb;
  })

  bench('arr.join("")', function () {
    var c = [longstra, longstrb].join('')
  })

  bench('str.concat', function () {
    var c = longstra.concat(longstrb)
  })
})